commits.content:
import { defineField, defineType } from 'sanity';

export const contactUs = defineType({
  name: 'contactUs',
  type: 'document',
  title: 'Contact Us',
  fields: [
    defineField({
      name: 'language',
      type: 'string',
      readOnly: true,
      hidden: true,
      validation: rule => rule.required(),
    }),
    defineField({
      name: 'title',
      type: 'string',
      title: 'Title',
      validation: rule => rule.required(),
    }),
    defineField({
      name: 'email',
      type: 'string',
      title: 'Email',
      validation: rule => rule.required().email(),
    }),
    defineField({
      name: 'officeHours',
      type: 'string',
      title: 'Office Hours',
      validation: rule => rule.required(),
    }),
    defineField({
      name: 'address',
      type: 'string',
      title: 'Address',
      validation: rule => rule.required(),
    }),
    defineField({
      name: 'mapLink',
      type: 'url',
      title: 'Map Link',
      validation: rule => rule.required().uri({
        allowRelative: true,
        scheme: ['http', 'https']
      }),
    }),
    defineField({
      name: 'phoneNumber',
      type: 'string',
      title: 'Phone Number',
      validation: rule => rule.required().regex(/^\+?[1-9]\d{1,14}$/, {
        name: 'Phone number',
        invert: false,
      }),
    }),
    defineField({
      name: 'image',
      type: 'image',
      title: 'Image',
      description: 'Optional image to display on the Contact Us section',
      options: {
        hotspot: true, // Enables the hotspot functionality for cropping images
      },
    }),
  ],
  preview: {
    select: {
      title: 'title',
      language: 'language',
    },
    prepare: ({ title, language }) => ({
      title: `${title} (${language})`,
    }),
  },
});
